mapper.select:
SELECT * FROM yunhe.mall_goods_item a WHERE id IN (SELECT goods_item_id FROM yunhe.mall_goods_category_and_goods_item b WHERE b.goods_name LIKE concat('%', #{wd}, '%') OR b.goods_item_name LIKE concat('%', #{wd}, '%') OR category_level1_name LIKE concat('%', #{wd}, '%') OR category_level2_name LIKE concat('%', #{wd}, '%') OR category_level3_name LIKE concat('%', #{wd}, '%'))
ORDER BY a.create_time DESC;
ORDER BY a.price DESC;
ORDER BY a.price ASC;
